(function(){tinymce.PluginManager.requireLangPack('picturetag');tinymce.create('tinymce.plugins.PictureTagPlugin',{init:function(ed,url){ed.addCommand('mcePictureTag',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+ed.getLang('example.delta_width',0),height:120+ed.getLang('example.delta_height',0),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('picturetag',{title:'PictureTagger.desc',cmd:'mcePictureTag',image:url+'/img/picturetag.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('picturetag',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Picturetagger Plugin',author:'embedded Projects GmbH',authorurl:'http://eproo.net',infourl:'http://eproo.net',version:"1.0"}}});tinymce.PluginManager.add('picturetag',tinymce.plugins.PictureTagPlugin)})();
